mapper.select:
SELECT p.product_id, p.name, p.price, p.manufacturer, p.image_path
FROM product p
JOIN category c on p.category_id = c.category_id
LEFT JOIN liked l on p.product_id = l.product_id
WHERE p.status = #{status}

    AND c.name = #{category}

GROUP BY p.product_id, p.price


        ORDER BY COUNT(l.like_id) DESC


                LIMIT ${pageSize}
                OFFSET ${offset}


                LIMIT 20
                OFFSET ${offset}


                LIMIT ${pageSize}


                LIMIT 20




        ORDER BY p.price ASC


                LIMIT ${pageSize}
                OFFSET ${offset}


                LIMIT 20
                OFFSET ${offset}


                LIMIT ${pageSize}


                LIMIT 20
